import React, { Component, useState } from 'react'

/* IMPORT OUR GQL */
import {useQuery} from '@apollo/client';
/* IMPORT THE QUERY */
import {getAuthorsQuery} from '../queries/queries';
/* THE HOOK */
const Addbook = () =>{
/* FORM GET */
const[_formstate,_setForm]=useState(null);

/* MAKE THE QUERY AND DESTRUCT IT */
const { loading, error, data } = useQuery(getAuthorsQuery);
/* IF IT IS LOADING */
if (loading) return <p>Loading...</p>;
/* IF IT TROHWS AN ERROR */
if (error) return <p>Error :</p>;

return (
<div>
{/* FORM ADD BOOK */}
<form id="add-book">
        {/* div 1 */}
        <div className="field">
        {/* label */}
          <label>Book name:</label>
          {/* INPUT BOX */}
          <input type="text" onChange={(e)=>_setForm({..._formstate,name: e.target.event})}/>
        </div>
        {/* div 2 */}
        <div className="field">
          {/* label */}
          <label>Genre:</label>
          {/* INPUT BOX */}
          <input type="text" onChange={(e)=>_setForm({..._formstate,genre: e.target.event})}/>
        </div>
        {/* div 3 */}
        <div className="field">
          {/* label */}
          <label>Author:</label>
          <select onChange={(e)=>_setForm({..._formstate,authorId: e.target.value})}>
            {/* MAPPING OF OPTIONS */}
            <option>Select the author</option>
            {
            /* MAPPING THE DATA TO GET THE NAME OF THE AUTHORS */

            data.authors.map(({name,id})=>{
            /* RETURN THE OPTIONS */
            return <option key={id} value={id}>{name}</option>
            })
            }
          </select>
        </div>

        <button onClick={()=>{console.log(_formstate)}}>+</button>

      </form>
</div>
)

}


/* EXPORT IT */
export default Addbook;
