import React, { Component, useEffect, useState } from 'react'

/* IMPORT OUR GQL */
import {useQuery,useMutation} from '@apollo/client';
/* IMPORT THE QUERY */
import {getAuthorsQuery,addBookMutation} from '../queries/queries';
/* THE HOOK */
const Addbook = () =>{
/* FORM GET */
const[_formstate,_setForm]=useState(null);



/* MAKE THE MUTATION AND DETRUCT IT */
const [addBooks, { data_mutation, loading_mutation, error_mutation }] = useMutation(addBookMutation);

/* MAKE THE QUERY AND DESTRUCT IT */
const { loading, error, data } = useQuery(getAuthorsQuery);
/* IF IT IS LOADING */
if (loading) return <p>Loading...</p>;
/* IF IT TROHWS AN ERROR */
if (error) return <p>Error :</p>;

/* SUBMIT FUNCTION */
const submit = (e) =>{
    /* PREVENT THE REFRESH */
    e.preventDefault();
    /* GRAB DE VARS */
    var name=_formstate.name;
    var genre= _formstate.genre;
    var authorId=_formstate.authorId;
    console.log(name);
    console.log(genre);
    console.log(authorId);
    /* MAKE THE MUTATION */
    addBooks({ variables: {name,genre,authorId} });
    console.log(data_mutation);
    console.log(loading_mutation);
    console.log(error_mutation);
    }

return (
<div>
{/* FORM ADD BOOK */}
<form id="add-book" onSubmit={submit}>
        {/* div 1 */}
        <div className="field">
        {/* label */}
          <label>Book name:</label>
          {/* INPUT BOX */}
          <input type="text"  onChange={(e)=>{_setForm({..._formstate,name: e.target.value})}}/>
        </div>
        {/* div 2 */}
        <div className="field">
          {/* label */}
          <label>Genre:</label>
          {/* INPUT BOX */}
          <input type="text" onChange={(e)=>_setForm({..._formstate,genre: e.target.value})}/>
        </div>
        {/* div 3 */}
        <div className="field">
          {/* label */}
          <label>Author:</label>
          <select onChange={(e)=>_setForm({..._formstate,authorId: e.target.value})}>
            {/* MAPPING OF OPTIONS */}
            <option>Select the author</option>
            {
            /* MAPPING THE DATA TO GET THE NAME OF THE AUTHORS */

            data.authors.map(({name,id})=>{
            /* RETURN THE OPTIONS */
            return <option key={id} value={id}>{name}</option>
            })
            }
          </select>
        </div>

        <button>+</button>

      </form>
</div>
)

}


/* EXPORT IT */
export default Addbook;
