/* IMPORT OUR GQL */
import {gql} from '@apollo/client';
/* OUR QUERY */
const getBooksQuery = gql`
query GetBooks{
books{
name
id    
}
}
`

/* OUR QUERY */
const getAuthorsQuery = gql`
query GetAuthors{
authors{
name
id  
}
}
`
/* ADD BOOK MUTATION */
const addBook = gql`
  mutation addBook($name: String!,$genre: String!,$authorId: String!) {
    addBook(name: $name,genre: $genre, authorId: $authorId) {
      name
      genre
    }
  }
`;




/* EXPORT IT */
export {getBooksQuery,getAuthorsQuery,addBook};
