/* IMPORT OUR GQL */
import {gql} from '@apollo/client';
/* OUR QUERY */
const getBooksQuery = gql`
query GetBooks{
books{
name
id    
}
}
`

/* OUR QUERY */
const getAuthorsQuery = gql`
query GetAuthors{
authors{
name
id  
}
}
`
/* ADD BOOK MUTATION */
const addBookMutation=gql`
mutation addBook{
    addBook(name: "Pedro" , genre: "Musica" , authorId: "62ada9c0ff455ed538bc7d57"){
    name
    genre
    authorId
    }
}
`




/* EXPORT IT */
export {getBooksQuery,getAuthorsQuery,addBookMutation};
