import React, { Component } from 'react'
/* IMPORT OUR GQL */
import {gql,useQuery} from '@apollo/client';
/* OUR QUERY */
const getBooksQuery = gql`
query GetBooks{
books{
name
id    
}
}
`

function Test(){
    const { loading, error, data } = useQuery(getBooksQuery);
    console.log(data);  
}

/* COMPONENT BOOK LIST */
class BookList extends Component {
  render() {
    /* RETURN JSX */
    return (
      /* LIST DIV */
      <div>
      {/* LIST */}
      <u id='book-list'>
      <li>Book name</li>
      </u>

      </div>
    )
  }
}
/* EXPORT IT */
export default BookList
